	public static int Solve(String input) {
		Pattern patternOpen = Pattern.compile("\\(");
		Matcher matcherOpen = patternOpen.matcher(input);
		int count = 0;
		while (matcherOpen.find()) {
		    count++;
		}
		Pattern patternClosed = Pattern.compile("\\)");
		Matcher matcherClosed = patternClosed.matcher(input);
		int countClosed = 0;
		while (matcherClosed.find()) {
			countClosed++;
		}
		 
		System.out.println(count + " and " + countClosed);
		
		int total = count - countClosed;
		
		return total;
		
	}
	public static void main(String[] args) {
			Solve(input);
	}
